var id = "1"
var Low= global.get("Low1");
var LowCritical= global.get("LowCritical1");
var High= global.get("High1");
var HighCritical= global.get("HighCritical1");
var para=global.get('ParaDrop[1]')
var Alert = para.name;
var LiveValue = global.get('sensorData[1]');


if(LiveValue<LowCritical)
{
    msg.background = "red"
    msg.color = "white"
    msg.label = Alert + " Low Critical: " + flow.get("id1LowCriticalCount")
    flow.set("id1LowCriticalCount", flow.get("id1LowCriticalCount") + 1)
    node.send(msg)
}

else if((LowCritical<LiveValue)&&(LiveValue<Low))
{
    msg.background = "red"
    msg.color = "white"
    msg.label = Alert + " Low: " + flow.get("id1LowCount")
    flow.set("id1LowCount", flow.get("id1LowCount") + 1)
    node.send(msg)
}

else if((Low<LiveValue)&&(LiveValue<High))
{
    msg.background = "transparent"
    msg.color = "white"
    msg.label = ""
    flow.set("id1LowCriticalCount",0)
    flow.set("id1LowCount",0)
    flow.set("id1HighCount",0)
    flow.set("id1HighCriticalCount", 0)
    node.send(msg)
}

else if((High<LiveValue)&&(LiveValue<HighCritical))
{
    msg.background = "red"
    msg.color = "white"
    msg.label = Alert + " High: " + flow.get("id1HighCount")
    flow.set("id1HighCount", flow.get("id1HighCount") + 1)
    node.send(msg)
}

else if(LiveValue > HighCritical)
{
    msg.background = "red"
    msg.color = "white"
    msg.label = Alert + " High Critical: " + flow.get("id1HighCriticalCount")
    flow.set("id1HighCriticalCount", flow.get("id1HighCriticalCount") + 1)
    node.send(msg)
}
return null;

